     1→'use client';
     2→
     3→import { createContext, useContext, useEffect, useState, type ReactNode } from 'react';
     4→type Theme = 'light' | 'dark';
     5→
     6→export interface AccentOption {
     7→  key: string;
     8→  label: string;
     9→  color: string;
    10→}
    11→
    12→export const accentOptions: AccentOption[] = [
    13→  { key: 'grey', label: 'Grey (default)', color: '#6b7280' },
    14→  { key: 'purple', label: 'Purple', color: '#6366f1' },
    15→  { key: 'green', label: 'Green', color: '#22c55e' },
    16→  { key: 'cyan', label: 'Cyan', color: '#06b6d4' },
    17→  { key: 'magenta', label: 'Magenta', color: '#ec4899' },
    18→];
    19→
    20→interface ThemeContextValue {
    21→  theme: Theme;
    22→  toggleTheme: () => void;
    23→  setTheme: (theme: Theme) => void;
    24→  accent: string;
    25→  setAccent: (key: string) => void;
    26→}
    27→
    28→const ThemeContext = createContext<ThemeContextValue | undefined>(undefined);
    29→
    30→function applyAccent(key: string) {
    31→  const opt = accentOptions.find(o => o.key === key) || accentOptions[0];
    32→  document.documentElement.style.setProperty('--accent-color', opt.color);
    33→}
    34→
    35→export function ThemeProvider({ children }: { children: ReactNode }) {
    36→  const [theme, setThemeState] = useState<Theme>('light');
    37→  const [accent, setAccentState] = useState('grey');
    38→
    39→  useEffect(() => {
    40→    const stored = localStorage.getItem('aerwave-theme') as Theme | null;
    41→    if (stored) {
    42→      setThemeState(stored);
    43→      document.documentElement.classList.toggle('dark', stored === 'dark');
    44→    } else if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
    45→      setThemeState('dark');
    46→      document.documentElement.classList.add('dark');
    47→    }
    48→    // Load accent from localStorage
    49→    const localAccent = localStorage.getItem('aerwave-accent') || 'grey';
    50→    setAccentState(localAccent);
    51→    applyAccent(localAccent);
    52→  }, []);
    53→
    54→  const setTheme = (newTheme: Theme) => {
    55→    setThemeState(newTheme);
    56→    localStorage.setItem('aerwave-theme', newTheme);
    57→    document.documentElement.classList.toggle('dark', newTheme === 'dark');
    58→  };
    59→
    60→  const toggleTheme = () => {
    61→    setTheme(theme === 'light' ? 'dark' : 'light');
    62→  };
    63→
    64→  const setAccent = (key: string) => {
    65→    setAccentState(key);
    66→    localStorage.setItem('aerwave-accent', key);
    67→    applyAccent(key);
    68→  };
    69→
    70→  return (
    71→    <ThemeContext.Provider value={{ theme, toggleTheme, setTheme, accent, setAccent }}>
    72→      {children}
    73→    </ThemeContext.Provider>
    74→  );
    75→}
    76→
    77→export function useTheme() {
    78→  const context = useContext(ThemeContext);
    79→  if (context === undefined) {
    80→    throw new Error('useTheme must be used within a ThemeProvider');
    81→  }
    82→  return context;
    83→}
    84→

