     1→/* ============================================
     2→   AERWAVE COMPONENT CLASSES
     3→   Pages depend on these — do not remove
     4→   ============================================ */
     5→
     6→/* Shared card style */
     7→.card {
     8→  background: hsl(var(--card));
     9→  border: 1px solid hsl(var(--border));
    10→  border-radius: 8px;
    11→  padding: 1.5rem;
    12→}
    13→.card:hover { border-color: var(--accent-color, hsl(var(--primary))); }
    14→
    15→/* Status badges */
    16→.badge {
    17→  display: inline-flex;
    18→  align-items: center;
    19→  padding: 0.25rem 0.75rem;
    20→  border-radius: 9999px;
    21→  font-size: 0.75rem;
    22→  font-weight: 500;
    23→  text-transform: uppercase;
    24→  letter-spacing: 0.05em;
    25→}
    26→.badge-active { background: color-mix(in srgb, var(--success) 12%, transparent); color: var(--success); }
    27→.badge-paused { background: color-mix(in srgb, var(--warning) 12%, transparent); color: var(--warning); }
    28→.badge-draft { background: hsl(var(--muted-foreground) / 0.12); color: hsl(var(--muted-foreground)); }
    29→.badge-completed { background: color-mix(in srgb, var(--info) 12%, transparent); color: var(--info); }
    30→
    31→/* Button styles */
    32→.btn {
    33→  display: inline-flex;
    34→  align-items: center;
    35→  gap: 0.5rem;
    36→  padding: 0.5rem 1rem;
    37→  border-radius: 8px;
    38→  font-size: 0.875rem;
    39→  font-weight: 500;
    40→  cursor: pointer;
    41→  transition: all 0.15s;
    42→  border: none;
    43→  outline: none;
    44→}
    45→.btn-primary { background: var(--accent-color, hsl(var(--primary))); color: hsl(var(--primary-foreground)); }
    46→.btn-primary:hover { opacity: 0.9; }
    47→.btn-secondary { background: hsl(var(--muted)); color: hsl(var(--foreground)); border: 1px solid hsl(var(--border)); }
    48→.btn-secondary:hover { border-color: var(--accent-color, hsl(var(--primary))); }
    49→.btn-danger { background: color-mix(in srgb, var(--danger) 12%, transparent); color: var(--danger); }
    50→.btn-danger:hover { background: color-mix(in srgb, var(--danger) 25%, transparent); }
    51→
    52→/* Table styles */
    53→.table-container {
    54→  overflow-x: auto;
    55→  border: 1px solid hsl(var(--border));
    56→  border-radius: 8px;
    57→  background: hsl(var(--card));
    58→}
    59→.dark .table-container {
    60→  background: hsl(var(--glass-bg));
    61→  border-color: hsl(var(--glass-border));
    62→}
    63→table { width: 100%; border-collapse: collapse; }
    64→th {
    65→  text-align: left;
    66→  padding: 0.75rem 1rem;
    67→  font-size: 0.75rem;
    68→  font-weight: 600;
    69→  text-transform: uppercase;
    70→  letter-spacing: 0.05em;
    71→  color: hsl(var(--muted-foreground));
    72→  background: hsl(var(--muted) / 0.5);
    73→  border-bottom: 1px solid hsl(var(--border));
    74→}
    75→td {
    76→  padding: 0.75rem 1rem;
    77→  font-size: 0.875rem;
    78→  border-bottom: 1px solid hsl(var(--border));
    79→}
    80→tr:hover td { background: hsl(var(--muted) / 0.3); }
    81→tr:last-child td { border-bottom: none; }
    82→
    83→/* Stat card */
    84→.stat-card {
    85→  background: hsl(var(--card));
    86→  border: 1px solid hsl(var(--border));
    87→  border-radius: 8px;
    88→  padding: 0.75rem 1.25rem 1.25rem;
    89→  transition: border-color 0.15s;
    90→}
    91→.stat-card:hover { border-color: var(--accent-color, hsl(var(--primary))); }
    92→.stat-value { font-size: 1.75rem; font-weight: 700; letter-spacing: -0.02em; }
    93→.stat-label {
    94→  font-size: 0.75rem;
    95→  color: hsl(var(--muted-foreground));
    96→  text-transform: uppercase;
    97→  letter-spacing: 0.05em;
    98→  margin-top: 0.25rem;
    99→}
   100→
   101→/* Input styles */
   102→input, select, textarea {
   103→  background: hsl(var(--secondary));
   104→  border: 1px solid hsl(var(--border));
   105→  border-radius: 8px;
   106→  padding: 0.5rem 0.75rem;
   107→  color: hsl(var(--foreground));
   108→  font-size: 0.875rem;
   109→  width: 100%;
   110→  outline: none;
   111→  transition: border-color 0.15s;
   112→}
   113→input:focus, select:focus, textarea:focus { border-color: var(--accent-color, hsl(var(--primary))); }
   114→label {
   115→  display: block;
   116→  font-size: 0.75rem;
   117→  font-weight: 600;
   118→  color: hsl(var(--muted-foreground));
   119→  text-transform: uppercase;
   120→  letter-spacing: 0.05em;
   121→  margin-bottom: 0.375rem;
   122→}
   123→
   124→/* Section heading */
   125→.section-heading {
   126→  font-size: 0.875rem;
   127→  font-weight: 600;
   128→  text-transform: uppercase;
   129→  letter-spacing: 0.05em;
   130→  margin-bottom: 1rem;
   131→}
   132→.section-heading.mb-0 { margin-bottom: 0; }
   133→
   134→/* Detail row */
   135→.detail-row {
   136→  display: flex;
   137→  justify-content: space-between;
   138→  align-items: center;
   139→  padding: 0.5rem 0;
   140→  border-bottom: 1px solid var(--border);
   141→}
   142→.detail-row-label {
   143→  font-size: 0.6875rem;
   144→  color: var(--text-muted);
   145→  text-transform: uppercase;
   146→  letter-spacing: 0.05em;
   147→}
   148→
   149→/* Page header */
   150→.page-header { display: flex; align-items: center; justify-content: space-between; margin-bottom: 1rem; }
   151→.page-title { font-size: 1.5rem; font-weight: 700; letter-spacing: -0.02em; }
   152→
   153→/* Empty state */
   154→.empty-state { text-align: center; padding: 4rem 2rem; color: hsl(var(--muted-foreground)); }
   155→.empty-state h3 { font-size: 1.125rem; color: hsl(var(--muted-foreground)); margin-bottom: 0.5rem; }
   156→
   157→/* Grid layouts */
   158→.stats-grid {
   159→  display: grid;
   160→  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
   161→  gap: 0.675rem;
   162→}
   163→
   164→/* Tabs */
   165→.tabs { display: flex; gap: 0; border-bottom: 1px solid hsl(var(--border)); margin-bottom: 1.5rem; }
   166→.tab {
   167→  padding: 0.75rem 1.25rem;
   168→  font-size: 0.875rem;
   169→  color: hsl(var(--muted-foreground));
   170→  cursor: pointer;
   171→  border-bottom: 2px solid transparent;
   172→  transition: all 0.15s;
   173→  background: none;
   174→  border-top: none;
   175→  border-left: none;
   176→  border-right: none;
   177→}
   178→.tab:hover { color: hsl(var(--foreground)); }
   179→.tab.active { color: var(--accent-color, hsl(var(--primary))); border-bottom-color: var(--accent-color, hsl(var(--primary))); }
   180→
   181→/* Loading */
   182→.loading-spinner {
   183→  width: 24px; height: 24px;
   184→  border: 2px solid hsl(var(--border));
   185→  border-top-color: var(--accent-color, hsl(var(--primary)));
   186→  border-radius: 50%;
   187→  animation: spin 0.6s linear infinite;
   188→}
   189→
   190→/* Chart placeholder */
   191→.chart-placeholder {
   192→  background: hsl(var(--card));
   193→  border: 1px solid hsl(var(--border));
   194→  border-radius: 12px;
   195→  padding: 2rem;
   196→  display: flex;
   197→  align-items: center;
   198→  justify-content: center;
   199→  min-height: 240px;
   200→  color: hsl(var(--muted-foreground));
   201→  font-size: 0.875rem;
   202→}
   203→
   204→/* Progress bar */
   205→.progress-bar { height: 6px; background: hsl(var(--muted)); border-radius: 3px; overflow: hidden; }
   206→.progress-fill { height: 100%; background: var(--accent-color, hsl(var(--primary))); border-radius: 3px; transition: width 0.3s ease; }
   207→
   208→/* Transcript block */
   209→.transcript-block {
   210→  background: hsl(var(--secondary));
   211→  border: 1px solid hsl(var(--border));
   212→  border-radius: 8px;
   213→  padding: 1rem 1.25rem;
   214→  margin-bottom: 0.75rem;
   215→}
   216→.transcript-speaker { font-size: 0.75rem; font-weight: 600; text-transform: uppercase; letter-spacing: 0.05em; margin-bottom: 0.375rem; }
   217→.transcript-text { font-size: 0.875rem; line-height: 1.5; color: hsl(var(--foreground)); }
   218→
   219→/* Nav active item — theme-aware */
   220→.nav-item-active {
   221→  background: color-mix(in srgb, var(--accent-color, #6b7280) 12%, transparent);
   222→}
   223→.dark .nav-item-active {
   224→  background: color-mix(in srgb, var(--accent-color, #6b7280) 18%, transparent);
   225→}
   226→
   227→/* Doc layout — content + TOC sidebar */
   228→.doc-layout {
   229→  display: flex;
   230→  gap: 2rem;
   231→}
   232→.doc-layout-content {
   233→  flex: 1;
   234→  min-width: 0;
   235→}
   236→.doc-layout-toc {
   237→  width: 180px;
   238→  flex-shrink: 0;
   239→  position: sticky;
   240→  top: 1rem;
   241→  align-self: flex-start;
   242→  max-height: calc(100vh - 8rem);
   243→  overflow-y: auto;
   244→}
   245→.doc-toc-item {
   246→  display: block;
   247→  font-size: 0.75rem;
   248→  padding: 0.25rem 0;
   249→  color: #8a8a8a;
   250→  text-decoration: none;
   251→  border-left: 2px solid transparent;
   252→  padding-left: 0.75rem;
   253→  transition: color 0.15s, border-color 0.15s;
   254→}
   255→.doc-toc-item:hover {
   256→  color: hsl(var(--foreground));
   257→}
   258→.doc-toc-h3 {
