     1→/* Status dots with pulse */
     2→.status-dot {
     3→  width: 10px;
     4→  height: 10px;
     5→  border-radius: 50%;
     6→  display: inline-block;
     7→}
     8→.status-dot.healthy {
     9→  background: var(--success);
    10→  box-shadow: 0 0 8px var(--success);
    11→  animation: pulse-green 2s infinite;
    12→}
    13→.status-dot.degraded {
    14→  background: var(--warning);
    15→  box-shadow: 0 0 8px var(--warning);
    16→  animation: pulse-yellow 2s infinite;
    17→}
    18→.status-dot.down {
    19→  background: var(--danger);
    20→  box-shadow: 0 0 8px var(--danger);
    21→  animation: pulse-red 2s infinite;
    22→}
    23→
    24→@keyframes pulse-green {
    25→  0%, 100% { box-shadow: 0 0 4px var(--success); }
    26→  50% { box-shadow: 0 0 12px var(--success); }
    27→}
    28→@keyframes pulse-yellow {
    29→  0%, 100% { box-shadow: 0 0 4px var(--warning); }
    30→  50% { box-shadow: 0 0 12px var(--warning); }
    31→}
    32→@keyframes pulse-red {
    33→  0%, 100% { box-shadow: 0 0 4px var(--danger); }
    34→  50% { box-shadow: 0 0 12px var(--danger); }
    35→}
    36→
    37→/* Glassmorphism card */
