     9→
    10→class ApiError extends Error {
    11→  status: number;
    12→  constructor(message: string, status: number) {
    13→    super(message);
    14→    this.status = status;
    15→    this.name = "ApiError";
    16→  }
    17→}
    18→
    19→class ApiClient {
    20→  private baseUrl: string;
    21→  private tenantId: string | null = null;
    22→
    23→  constructor(baseUrl: string) {
    24→    this.baseUrl = baseUrl;
    25→  }
    26→
    27→  setTenant(tenantId: string) {
    28→    this.tenantId = tenantId;
    29→  }
    30→
    31→  private async request<T>(path: string, opts: RequestOptions = {}): Promise<T> {
    32→    const { method = "GET", body, headers = {} } = opts;
    33→
    34→    const reqHeaders: Record<string, string> = {
    35→      "Content-Type": "application/json",
    36→      ...headers,
    37→    };
    38→
    39→    if (this.tenantId) {
    40→      reqHeaders["x-tenant-id"] = this.tenantId;
    41→    }
    42→    if (opts.tenantId) {
    43→      reqHeaders["x-tenant-id"] = opts.tenantId;
    44→    }
    45→
    46→    const res = await fetch(`${this.baseUrl}${path}`, {
    47→      method,
    48→      headers: reqHeaders,
    49→      body: body ? JSON.stringify(body) : undefined,
    50→    });
    51→
    52→    if (!res.ok) {
    53→      const msg = await res.text().catch(() => "Unknown error");
    54→      throw new ApiError(msg, res.status);
    55→    }
    56→
    57→    return res.json();
    58→  }
