     1→'use client';
     2→
     3→import { createContext, useContext, useEffect, useState, type ReactNode } from 'react';
     4→import { api } from '@/lib/api';
     5→
     6→type Theme = 'light' | 'dark';
     7→
     8→export interface AccentOption {
     9→  key: string;
    10→  label: string;
    11→  color: string;
    12→}
    13→
    14→export const accentOptions: AccentOption[] = [
    15→  { key: 'grey', label: 'Grey (default)', color: '#6b7280' },
    16→  { key: 'purple', label: 'Purple', color: '#6366f1' },
    17→  { key: 'green', label: 'Green', color: '#22c55e' },
    18→  { key: 'cyan', label: 'Cyan', color: '#06b6d4' },
    19→  { key: 'magenta', label: 'Magenta', color: '#ec4899' },
    20→];
    21→
    22→interface ThemeContextValue {
    23→  theme: Theme;
    24→  toggleTheme: () => void;
    25→  setTheme: (theme: Theme) => void;
    26→  accent: string;
    27→  setAccent: (key: string) => void;
    28→}
    29→
    30→const ThemeContext = createContext<ThemeContextValue | undefined>(undefined);
    31→
    32→function applyAccent(key: string) {
    33→  const opt = accentOptions.find(o => o.key === key) || accentOptions[0];
    34→  document.documentElement.style.setProperty('--accent-color', opt.color);
    35→}
    36→
    37→export function ThemeProvider({ children }: { children: ReactNode }) {
    38→  const [theme, setThemeState] = useState<Theme>('light');
    39→  const [accent, setAccentState] = useState('grey');
    40→
    41→  useEffect(() => {
    42→    const stored = localStorage.getItem('aerwave-theme') as Theme | null;
    43→    if (stored) {
    44→      setThemeState(stored);
    45→      document.documentElement.classList.toggle('dark', stored === 'dark');
    46→    } else if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
    47→      setThemeState('dark');
    48→      document.documentElement.classList.add('dark');
    49→    }
    50→    // Load accent: localStorage first (instant), then sync from API
    51→    const localAccent = localStorage.getItem('aerwave-accent') || 'grey';
    52→    setAccentState(localAccent);
    53→    applyAccent(localAccent);
    54→    api.getSettings()
    55→      .then((res: unknown) => {
    56→        const settings = res as Record<string, string>;
    57→        if (settings?.accent_color && settings.accent_color !== localAccent) {
    58→          setAccentState(settings.accent_color);
    59→          localStorage.setItem('aerwave-accent', settings.accent_color);
    60→          applyAccent(settings.accent_color);
    61→        }
    62→      })
    63→      .catch(() => {});
    64→  }, []);
    65→
    66→  const setTheme = (newTheme: Theme) => {
    67→    setThemeState(newTheme);
    68→    localStorage.setItem('aerwave-theme', newTheme);
    69→    document.documentElement.classList.toggle('dark', newTheme === 'dark');
    70→  };
    71→
    72→  const toggleTheme = () => {
    73→    setTheme(theme === 'light' ? 'dark' : 'light');
    74→  };
    75→
    76→  const setAccent = (key: string) => {
    77→    setAccentState(key);
    78→    localStorage.setItem('aerwave-accent', key);
    79→    applyAccent(key);
    80→    // Persist to backend (fire and forget)
    81→    api.updateSettings({ accent_color: key }).catch(() => {});
    82→  };
    83→
    84→  return (
    85→    <ThemeContext.Provider value={{ theme, toggleTheme, setTheme, accent, setAccent }}>
    86→      {children}
    87→    </ThemeContext.Provider>
    88→  );
    89→}
    90→
    91→export function useTheme() {
    92→  const context = useContext(ThemeContext);
    93→  if (context === undefined) {
    94→    throw new Error('useTheme must be used within a ThemeProvider');
    95→  }
    96→  return context;
    97→}
    98→

