     7→interface ThemeContextType {
     8→  theme: Theme;
     9→  toggleTheme: () => void;
    10→  setTheme: (theme: Theme) => void;
    11→}
    12→
    13→const ThemeContext = createContext<ThemeContextType | undefined>(undefined);
    14→
    15→export function ThemeProvider({ children }: { children: ReactNode }) {
    16→  const [theme, setThemeState] = useState<Theme>('light');
    17→  const [mounted, setMounted] = useState(false);
    18→
    19→  useEffect(() => {
    20→    setMounted(true);
    21→    // Check localStorage, default to light
    22→    const stored = localStorage.getItem('console-theme') as Theme | null;
    23→    const initial = stored || 'light';
    24→    setThemeState(initial);
    25→    document.documentElement.classList.toggle('dark', initial === 'dark');
    26→  }, []);
    27→
    28→  const setTheme = (newTheme: Theme) => {
    29→    setThemeState(newTheme);
    30→    localStorage.setItem('console-theme', newTheme);
    31→    document.documentElement.classList.toggle('dark', newTheme === 'dark');
    32→  };
    33→
    34→  const toggleTheme = () => {
    35→    setTheme(theme === 'light' ? 'dark' : 'light');
    36→  };
    37→
    38→  // Prevent flash during hydration
    39→  if (!mounted) {
    40→    return <>{children}</>;
    41→  }
    42→
    43→  return (
    44→    <ThemeContext.Provider value={{ theme, toggleTheme, setTheme }}>
    45→      {children}
    46→    </ThemeContext.Provider>
    47→  );
    48→}
    49→
    50→export function useTheme() {
    51→  const context = useContext(ThemeContext);
    52→  if (!context) {
    53→    throw new Error('useTheme must be used within a ThemeProvider');
    54→  }
    55→  return context;
    56→}
